import React, { useState, useEffect } from 'react'
import { StyleSheet, View, Text, Image, FlatList, TouchableOpacity, RefreshControl } from 'react-native'

import firebase from 'firebase'
require('firebase/firestore')
import { connect } from 'react-redux'

const wait = (timeout) => {
    return new Promise(resolve => setTimeout(resolve, timeout));
}

function Feed(props) {

    const [refreshing, setRefreshing] = React.useState(false);
    const onRefresh = React.useCallback(() => {
        setRefreshing(true);
        wait(2000).then(() => setRefreshing(false));
    }, []);


    const [posts, setPosts] = useState([]);

    useEffect(() => {
        if (props.usersFollowingLoaded == props.following.length && props.following.length !== 0) {
            props.feed.sort(function (x, y) {
                return x.creation - y.creation;
            })
            setPosts(props.feed);
        }
        console.log(posts)

    }, [props.usersFollowingLoaded, props.feed])

    const onLikePress = (userId, postId) => {
        firebase.firestore()
            .collection("posts")
            .doc(userId)
            .collection("userPosts")
            .doc(postId)
            .collection("likes")
            .doc(firebase.auth().currentUser.uid)
            .set({})
    }
    const onDislikePress = (userId, postId) => {
        firebase.firestore()
            .collection("posts")
            .doc(userId)
            .collection("userPosts")
            .doc(postId)
            .collection("likes")
            .doc(firebase.auth().currentUser.uid)
            .delete()
    }
    return (
        <View style={styles.container}>
            <View style={styles.containerGallery}>
                <FlatList
                    style={{ height: 3, }}
                    refreshControl={
                        <RefreshControl
                            refreshing={refreshing}
                            onRefresh={onRefresh}
                        />
                    }
                    numColumns={1}
                    horizontal={false}
                    data={posts}
                    renderItem={({ item }) => (
                        <View
                            style={styles.containerImage}>

                            <View style={styles.avatar}>
                                <View style={styles.circle}>
                                    <Text style={styles.img}>IMG</Text>
                                </View>
                                <View style={styles.nameA}>
                                    <Text style={styles.inNameA}>{item.user.name}</Text>
                                </View>

                            </View>
                            <View style={styles.viewCap}>
                                <Text style={styles.textCap}>{item.caption}</Text>
                            </View>
                            <Image
                                style={styles.image}
                                source={{ uri: item.downloadURL }}
                            />

                            <View style={styles.lico}>

                                {item.currentUserLike ?
                                    (
                                        <View style={styles.viewD}>
                                            <TouchableOpacity
                                                style={styles.dis}
                                                onPress={() => onDislikePress(item.user.uid, item.id)}>
                                                <Text style={styles.texttD}>DISLIKE</Text>
                                            </TouchableOpacity>
                                        </View>
                                    )
                                    :
                                    (
                                        <View style={styles.viewL}>
                                            <TouchableOpacity
                                                style={styles.like}
                                                onPress={() => onLikePress(item.user.uid, item.id)}>
                                                <Text style={styles.textt}>LIKE</Text>
                                            </TouchableOpacity>
                                        </View>
                                    )
                                }

                                <View style={styles.viewC}>
                                    <TouchableOpacity
                                        style={styles.commen}
                                        onPress={() => props.navigation.navigate('Comment', { postId: item.id, uid: item.user.uid })}
                                    >
                                        <Text style={styles.textt}>COMMENT</Text>
                                    </TouchableOpacity>
                                </View>
                            </View>

                            <View style={styles.split}></View>
                        </View>

                    )}

                />
            </View>
        </View>

    )
}

const styles = StyleSheet.create({
    viewCap: {
        borderWidth: 1,
        marginLeft: 10,
        marginRight:10,
        marginTop: 10,
        marginBottom: 10,
    },
    textCap: {
        fontSize: 20,
        color: '#ff1ac6',
        fontStyle: 'italic',
        fontWeight: '300',
    },
    split: {
        marginTop: 5,
        backgroundColor: 'gray',
        height: 12,
    },
    textt: {
        fontWeight: 'bold',
        color: '#00bfff',
    },
    texttD: {
        fontWeight: 'bold',
        color: 'white',
    },
    viewC: {
        width: 170,
        // borderWidth: 1,
        // borderColor: 'red',
        margin: 5,
    },
    viewL: {
        width: 170,
        // borderWidth: 1,
        // borderColor: 'red',
        margin: 5,
    },
    viewD: {
        margin: 5,
        width: 170,
        // borderWidth: 1,
        // borderColor: 'red',
    },

    lico: {
        flexDirection: 'row',
    },
    commen: {
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'white',
        borderWidth: 1,
        borderColor: '#00bfff',
        height: 50,
        borderRadius: 10,
    },
    dis: {
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 10,
        backgroundColor: '#00bfff',
        borderColor: '#00FFFF',
        borderWidth: 1,
        height: 50,
    },
    like: {
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: 10,
        borderColor: '#00bfff',
        backgroundColor: 'white',
        borderWidth: 1,
        height: 50,
    },
    img: {
        color: 'green',
        fontWeight: 'bold',
    },
    avatar: {
        flexDirection: 'row',
    },
    inNameA: {
        fontSize: 25,
        fontStyle: 'italic',
        fontWeight: 'bold',
        width: 285,
        // color:'blue',
    },
    nameA: {
        height: 50,
        // borderWidth: 2,
        alignItems: 'center',
        justifyContent: 'center',
        margin: 5,
        marginLeft: 0,
    },
    circle: {
        alignItems: 'center',
        justifyContent: 'center',
        borderWidth: 2,
        borderRadius: 50,
        height: 50,
        width: 50,
        marginLeft: 20,
        borderColor: 'red',
    },
    container: {
        flex: 1,
    },
    containerInfo: {
        margin: 20
    },
    containerGallery: {
        flex: 1
    },
    containerImage: {
        // flex: 1 / 3
    },
    image: {
        borderRadius: 10,
        marginLeft: 5,
        marginRight: 5,
        flex: 1,
        aspectRatio: 1 / 1
    }
})
const mapStateToProps = (store) => ({
    currentUser: store.userState.currentUser,
    following: store.userState.following,
    feed: store.usersState.feed,
    usersFollowingLoaded: store.usersState.usersFollowingLoaded,


})
export default connect(mapStateToProps, null)(Feed);
